import urllib.request as req

# xml, json => 대기업들이 제공하는 데이터, 공공기관에서 제공하는 데이터

# url - 이미지
# 이미지 주소 복사
img = "data:image/jpeg;base64,"
html = "https://www.google.com"

# 다운받을 경로
path1 = "C:/Users/sdedu/Desktop/data/webcrawling/wannabe.png"
path2 = "C:/Users/sdedu/Desktop/data/webcrawling/google.html"

# 예외처리
try:
    # urlretrieve() : 다운받을 파일 및 저장정보를 return
    f1, h1 = req.urlretrieve(img, path1)     
    f2, h2 = req.urlretrieve(html, path2)
except Exception as e:
    print(e)
    print("실패 !")
else:
    # 정상적으로 실행되었을 경우
    # Header 정보를 출력 : 개발자 도구 => Network => Headers
    print(h1)
    print("-------------")
    print(h2)
# 다운로드 파일 정보
print(f"이미지 정보 : {f1}")
print(f"파일 정보 : {f2}")

print("성공 !")